INSERT INTO `about_aboutus` (`id`, `subject`, `description`, `is_active`, `created_at`, `update_at`, `city`) VALUES
(1, 'رنک صفر, شرکت دیجیتال مارکتینگ', '<div style=\"align-self:center; display:flex; justify-content:center; width:100%\">\r\n<div class=\"markdown-container\" dir=\"ltr\" style=\"font-size:var(--font-size); line-height:var(--line-height)\">\r\n<div>\r\n<p dir=\"rtl\"><span style=\"font-size:16px\"><strong>صدرا آبادکار، مهندس نرم&zwnj;افزار متخصص</strong> در حوزه بک&zwnj;اند، پلتفرم و اپلیکیشن، در سال ۱۴۰۴ شرکت &ldquo;رنک صفر&rdquo; را با هدف کمک به شرکت&zwnj;های خصوصی، اصناف و تجار در توسعه کسب&zwnj;وکار در ایران و به ویژه تبریز تأسیس کرد. &ldquo;رنک صفر&rdquo; با تمرکز بر طراحی و توسعه وب&zwnj;سایت، به دنبال ارائه راهکارهای نوین و کارآمد برای حضور آنلاین کسب&zwnj;وکارها و بهبود جایگاه آن&zwnj;ها در فضای دیجیتال است.</span></p>\r\n\r\n<p dir=\"rtl\"><span style=\"font-size:16px\">آبادکار با تکیه بر دانش و تجربه خود در زمینه مهندسی نرم&zwnj;افزار، <strong>&ldquo;رنک صفر&rdquo;</strong> را بر پایه اصول کیفیت، نوآوری و مشتری&zwnj;مداری بنا نهاده است. این شرکت تلاش می&zwnj;کند تا با ارائه خدمات تخصصی و سفارشی&zwnj;سازی شده، نیازهای منحصربه&zwnj;فرد هر کسب&zwnj;وکار را به طور کامل برآورده سازد.</span></p>\r\n\r\n<p dir=\"rtl\"><span style=\"font-size:16px\">&ldquo;رنک صفر&rdquo; با بهره&zwnj;گیری از تیمی مجرب و متخصص در زمینه&zwnj;های مختلف طراحی وب، توسعه نرم&zwnj;افزار، بازاریابی دیجیتال و سئو، قادر است طیف گسترده&zwnj;ای از خدمات را به مشتریان خود ارائه دهد. این خدمات شامل طراحی وب&zwnj;سایت&zwnj;های شرکتی، فروشگاهی و شخصی، توسعه اپلیکیشن&zwnj;های موبایل، بهینه&zwnj;سازی وب&zwnj;سایت برای موتورهای جستجو، تولید محتوای جذاب و اثربخش، و مدیریت شبکه&zwnj;های اجتماعی می&zwnj;شود.</span></p>\r\n\r\n<p dir=\"rtl\"><span style=\"font-size:16px\">هدف اصلی &ldquo;رنک صفر&rdquo; ایجاد ارزش افزوده برای مشتریان خود از طریق ارائه راهکارهای دیجیتال مؤثر و مقرون&zwnj;به&zwnj;صرفه است. این شرکت معتقد است که با حضور قوی و حرفه&zwnj;ای در فضای آنلاین، کسب&zwnj;وکارها می&zwnj;توانند به طور قابل توجهی دامنه فعالیت خود را گسترش دهند، مشتریان جدید جذب کنند و در نهایت به سودآوری بیشتری دست یابند.</span></p>\r\n\r\n<p dir=\"rtl\"><span style=\"font-size:16px\">&ldquo;رنک صفر&rdquo; با درک اهمیت روزافزون فناوری اطلاعات در دنیای امروز، تلاش می&zwnj;کند تا با ارائه خدمات به&zwnj;روز و منطبق با آخرین استانداردهای جهانی، به مشتریان خود در رسیدن به اهداف تجاری&zwnj;شان کمک کند. این شرکت همواره به دنبال یادگیری و بهبود مستمر است و از جدیدترین تکنولوژی&zwnj;ها و روش&zwnj;های نوین در ارائه خدمات خود استفاده می&zwnj;کند.</span></p>\r\n\r\n<p dir=\"rtl\"><span style=\"font-size:16px\">&ldquo;رنک صفر&rdquo; با مدیریت صدرا آبادکار، به عنوان یک شرکت پیشرو در زمینه طراحی و توسعه وب&zwnj;سایت، متعهد به ارائه خدمات با کیفیت و ایجاد رضایت کامل در مشتریان خود است. این شرکت امیدوار است که با تلاش و پشتکار، بتواند نقش موثری در توسعه و پیشرفت کسب&zwnj;وکارهای ایرانی ایفا کند.</span></p>\r\n</div>\r\n</div>\r\n</div>', 1, '2025-04-04 09:59:18.000000', '2025-04-04 09:59:18.000000', '');
