INSERT INTO `company_faq` (`id`, `question`, `answer`, `is_active`, `register_date`, `video`) VALUES
(1, 'چه سایتی برای کسب‌وکارم مناسب است؟', 'برای کسب‌وکارهای کوچک، وردپرس گزینه‌ای خوب و مقرون‌به‌صرفه است. اما اگر کسب‌وکار بزرگی دارید، بهتر است سراغ سایت‌های اختصاصی با کدنویسی حرفه‌ای بروید.', 1, '2025-03-25 04:13:57.074087', ''),
(2, 'چرا باید به ما اعتماد کنید؟', 'حضور ما در نتایج برتر گوگل و همچنین عقد قرارداد رسمی، و نمونه کار هایی که داریم   نشان‌ دهنده تعهد ما به کیفیت و شفافیت در همکاری است.', 1, '2025-03-25 04:19:10.913666', ''),
(3, 'شرایط پرداخت هزینه‌ها چگونه است؟', 'مطابق شرایط، پس از انتخاب پلن موردنظر، نیمی از مبلغ به صورت نقدی پرداخت می‌شود و برای بخش باقی‌مانده، ضمانت‌هایی مانند چک یا سفته دریافت خواهد شد. این رویه صرفاً جهت تضمین تعهدات طرفین و ایجاد تفاهم کامل است.', 1, '2025-03-25 04:22:05.945840', ''),
(4, 'مزیت سایت یا اپلیکیشن برای من چیست ؟', 'داشتن سایت یا اپلیکیشن، باعث افزایش دیده شدن، جذب مشتری بیشتر، رشد فروش و دسترسی آسان‌ به خدمات شما می‌شود.', 1, '2025-03-25 04:28:46.124610', ''),
(5, 'شرایط پشتیبانی شما چگونه است ؟', 'تیم پشتیبانی ما به صورت 24/7 در دسترس است تا در هر ساعت از شبانه‌روز پاسخگوی نیازهای شما باشد.', 1, '2025-03-25 04:36:20.177241', '');
