(1, 'پشتیبانی 24 ساعتی شبانه روزی', '<p style=\"text-align:right\"><span style=\"color:#333333\"><span style=\"font-size:14px\">به هر دلیلی سایت یا پلتفرم شما به مشکل خورد ما موظف هستیم </span></span><span style=\"color:#e74c3c\"><span style=\"font-size:14px\">ساعت 3 نصف شب</span></span><span style=\"color:#333333\"><span style=\"font-size:14px\"> هم باشد ما باید پاسخ گوی شما عزیزان باشیم</span></span></p>', 1, '2025-03-25 18:22:23.000000', 'images/why_us/f1d32f9b-c912-4399-b522-04e8608a9b9dFV6Xam01.svg'),
(2, 'قیمت بسیار مناسب', '<p><span style=\"color:#333333\"><span style=\"font-size:14px\">شما میتوانید به راحتی با سرچ کردن قیمت هارو به سادگی مقایسه کنید</span></span></p>', 1, '2025-03-25 19:25:06.000000', 'images/why_us/10a9d29b-fb8a-49ac-b047-62f78087b3505tUayj01.svg'),
(3, 'تجربه کافی در بازار کار و متخصصان ماهر', '<p><span style=\"color:#333333\">&laquo;ما بیش از 4 سال تجربه حرفه&zwnj;ای در زمینه&nbsp; و فروش در بازار کار داریم و در این مدت موفق شده&zwnj;ایم با ارائه خدمات باکیفیت، نیازهای مشتریان خود را به بهترین شکل ممکن برآورده کنیم.&raquo;</span></p>', 1, '2025-03-25 19:39:24.000000', 'images/why_us/6fa136ac-b29f-4ed2-b409-b2e3e02df1bfwk6v3H01.svg');
INSERT INTO `company_why_us_2` (`id`, `subject`, `description`, `is_active`, `register_date`) VALUES
(1, 'بهترین را تجربه کنید', '<p dir=\"rtl\"><span style=\"color:#000000\"><span style=\"font-size:14px\">شرکت دیجیتال مارکتینگ با رنک صفر در تبریز، با بهره&zwnj;گیری از استراتژی&zwnj;های هوشمند، به کسب&zwnj;وکارها کمک می&zwnj;کند تا در صدر نتایج جستجو دیده شوند. افزایش بازدید، جذب مخاطب هدف و تقویت جایگاه آنلاین، از اولویت&zwnj;های این مجموعه حرفه&zwnj;ای است.</span></span></p>', 1, '2025-03-25 20:05:54.000000');
