INSERT INTO `service_faq` (`id`, `question`, `answer`, `is_active`, `register_date`, `service_id`) VALUES
(13, 'چقدر زمان می‌برد تا نتایج سئو را ببینم؟', 'معمولاً بین ۳ تا ۶ ماه طول می‌کشد تا تاثیر سئو به طور محسوس دیده شود.', 1, '2025-05-31 14:47:15.000000', 4),
(14, 'آیا فقط برای کسب‌وکارهای بزرگ سئو لازم است؟', 'خیر، کسب‌وکارهای کوچک و متوسط هم با سئو می‌توانند مشتریان بیشتری جذب کنند و در بازار محلی مثل تبریز رشد کنند.', 1, '2025-05-31 14:47:15.000000', 4),
(15, 'یا خدمات سئو در تبریز با توجه به بازار محلی تفاوت دارد؟', 'بله، استراتژی سئو محلی و کلمات کلیدی مرتبط با تبریز اهمیت ویژه‌ای دارد تا مخاطبان هدف به راحتی شما را پیدا کنند.', 1, '2025-05-31 14:47:15.000000', 4),
(17, 'چه تضمینی میدین برای پروژه مشتری که مشکل نخوره ؟', 'ما برای اطمینان از کیفیت پروژه شما، اول از همه یک قرارداد رسمی و شفاف تنظیم می‌کنیم تا خیال هر دو طرف راحت باشد و همه چیز دقیق و روشن پیش برود. دوم اینکه ما بدون هیچ شعار اضافه‌ای باور داریم که کار شما اعتبار ماست؛ وقتی شما بهترین باشید، ما با افتخار می‌گوییم این پروژه کار ما بوده. سوم هم اینکه ما در رتبه‌های برتر گوگل حضور داریم و برای رسیدن به این جایگاه زمان و انرژی زیادی صرف کردیم، بنابراین تعهد ما فقط یک وعده نیست، بلکه بخشی از مسیر حرفه‌ای ماست که با جدیت پیگیری می‌کنیم.', 1, '2025-06-05 06:32:59.000000', 5),
(18, 'چقدر زمان می‌بره تا سایت آماده بشه؟', 'واقعیت اینه که هر سایتی با توجه به امکانات، طراحی اختصاصی، پنل مدیریت، سئو اولیه و ویژگی‌های خاصی که مد نظر شماست، زمان متفاوتی نیاز داره. اما معمولاً برای سایت‌های شرکتی یا فروشگاهی که در سطح معمول طراحی می‌شن، بین 1 تا 2 ماه زمان لازم داریم تا پروژه به‌صورت اصولی و استاندارد آماده و تحویل داده بشه. البته ما همیشه در ابتدای پروژه، بر اساس آپشن‌های موردنیاز شما، زمان دقیق‌تری مشخص می‌کنیم تا همه چیز شفاف باشه.', 1, '2025-06-06 06:51:18.000000', 5),
(19, 'هاست و دامنه رو هم شما تهیه می‌کنید یا خودم باید بگیرم؟', 'اگه از قبل دامنه دارید، خیلی هم عالی! اما هاست باید مجدد بررسی یا تهیه بشه. اگه هاست قبلی دارید، ما اول تست می‌کنیم ببینیم از نظر فنی مناسب پروژه‌ شما هست یا نه. در غیر این صورت یا اگر هیچ‌کدوم از این موارد رو ندارید، اصلاً نگران نباشید؛ ما به‌صورت کامل تهیه‌ و راه‌اندازی هاست و دامنه رو براتون انجام می‌دیم تا همه چیز آماده تحویل باشه.', 1, '2025-06-06 06:51:18.000000', 5),
(20, 'اگه بعداً بخوام امکانات جدید به سایت اضافه کنم، ممکنه؟', 'بله، حتماً. شما هر زمان که نیاز به آپدیت یا اضافه کردن امکانات جدید داشتید، می‌تونید درخواست بدید. بعد از بررسی جزئیات و پرداخت هزینه مربوطه، به‌روزرسانی سایت شما در سریع‌ترین زمان ممکن انجام می‌شه.', 1, '2025-06-06 06:51:18.000000', 5),
(21, 'آیا آموزش کار با سایت رو بهم می‌دین؟', 'بله، حتماً. بعد از طراحی و تحویل سایت، آموزش کامل مدیریت سایت رو به‌صورت رایگان در اختیار شما قرار می‌دیم تا بتونید به‌راحتی محتوای سایت رو ویرایش یا به‌روزرسانی کنید. هدف ما اینه که شما بدون وابستگی، کنترل کامل سایت‌تون رو داشته باشید.', 1, '2025-06-06 06:51:18.000000', 5),
(22, 'یا نمونه‌کارهایی دارید که ببینم؟', 'اگه با سیستم وارد شدید، فقط کافیه یه مقدار صفحه رو اسکرول کنید. دکمه بزرگ \"مشاهده نمونه‌کارها\" رو می‌بینید. با کلیک روی اون، می‌تونید نمونه پروژه‌های ما رو به‌راحتی ببینید.\r\nالبته از منوی بالا هم می‌تونید مستقیم وارد بخش نمونه‌کارها بشید.', 1, '2025-06-06 06:51:18.000000', 5),
